    private void btn_aceptarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_aceptarActionPerformed
        String usuario = txt_usuario.getText().trim();
        String password = String.valueOf(txt_password.getPassword()).trim();

        if (usuario.isEmpty() || password.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Debe llenar todos los campos", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }

        conexionlocal conLocal = new conexionlocal();
        conLocal.conectar();
        Connection con = conLocal.getConexion();

        try {
            // contrase침as est치n encriptadas
            String sql = "SELECT u.*, r.nombre AS rol_nombre " 
           + "FROM usuario u " // <--- CL츼USULA FROM AGREGADA
           + "INNER JOIN rol r ON u.id_rol = r.id_rol "
           + "WHERE BINARY u.nombre_usuario = ? "
           + "AND u.password = SHA2(?, 256)";

            PreparedStatement ps = con.prepareStatement(sql);
            ps.setString(1, usuario);
            ps.setString(2, password);
            ResultSet rs = ps.executeQuery();

            if (rs.next()) {
                String rol = rs.getString("rol_nombre");
                JOptionPane.showMessageDialog(this, "Bienvenido " + usuario + " (" + rol + ")");

                // 游댳 Crear la ventana principal
                Principal p = new Principal();

                // 游댳 Mostrar opciones seg칰n el rol
                if (rol.equalsIgnoreCase("Administrador")) {
                    p.habilitarMenuAdmin();
                } else if (rol.equalsIgnoreCase("Vendedor")) {
                    p.habilitarMenuVendedor();
                } else if (rol.equalsIgnoreCase("Almacenero")) {
                    p.habilitarMenuVendedor();
                } else {
                    JOptionPane.showMessageDialog(this, "Rol no reconocido, acceso limitado");
                    p.habilitarMenuLimitado();
                }

                p.setVisible(true);
                this.dispose();
            } else {
                JOptionPane.showMessageDialog(this, "Usuario o contrase침a incorrectos", "Error", JOptionPane.ERROR_MESSAGE);
            }

        } catch (SQLException e) {
            JOptionPane.showMessageDialog(this, "Error de conexi칩n: " + e.getMessage());
        } finally {
            conLocal.desconectar();
        }

    }//GEN-LAST:event_btn_aceptarActionPerformed

